import './App.scss';
import React, { useState, useEffect, Component } from "react";
import { BrowserRouter as Router,Route, Routes as Switch, useHistory, useLocation, Navigate } from "react-router-dom";
import Home from './Home/Home'; 
import Skills from './Skills/Skills'; 
import Projects from './Projects/Projects'; 
import Notfound from './Notfound/Notfound';
import About from './About/About';
import { Routes } from '../routes';
import Navbar from '../components/Navbar/Navbar';
import { TransitionGroup, CSSTransition} from 'react-transition-group';



export default ()=>{
  const location = useLocation();
  return(
    <Router>
      <div className='main'>
        <TransitionGroup>
          <CSSTransition timeout={1000} className='fade' key={location.key}>
            <Navbar/>
            <Switch location={location}>
              <Route exact path={Routes.About.path} element={<About/>}/>
              <Route exact path={Routes.Home.path} element={<Home/>}/>
              <Route exact path={Routes.Skills.path} element={<Skills/>}/>
              <Route exact path={Routes.Projects.path} element={<Projects/>}/>
              <Route exact path={Routes.Notfound.path} element={<Notfound/>}/>
              <Route path={Routes.Notfound.path} element={<Notfound/>}/>
            </Switch>
          </CSSTransition>
        </TransitionGroup>

      </div>

    </Router>


  )
}
